<template>
  <div id="app">
    <button @click="loadingShow()">点击加载</button>
    <vue-loading-page type="text" v-if="show"></vue-loading-page>
    <vue-loading-page type="circle" v-if="show"></vue-loading-page>
    <vue-loading-page type="dot" v-if="show"></vue-loading-page>
  </div>
</template>

<script>
export default {
  name: 'app',
  data () {
    return {
      show: false
    }
  },
  methods: {
    loadingShow(){
      this.show = !this.show;
    }
  },
}
</script>

